import React, { Component } from 'react';
//import axios from 'axios';

export class Home extends Component {
  constructor(props) {
    super(props);
    //this.handleSearchTermSubmit = this.handleSearchTermSubmit.bind(this);
    this.state = {searchQuery: '', res: []};
    this.handleChange = this.handleChange.bind(this);
    this.Search = this.Search.bind(this);
  }

  handleChange(event) {
    this.setState({searchQuery: event.target.value});
  }

  Search(event) { //SEND TO SERVER WITH THE QUERY
    const apiBaseUrl = 'https://localhost:44383/search/';
    var searchQ = `${this.state.searchQuery.split(/ +/).join('+')}`;
    event.preventDefault();
    fetch(apiBaseUrl + searchQ)
            .then(response => {response.json();})
            .then(d => {
              this.setState({ res: d });
              console.log("state", this.state.res)
            })
            .catch(error => console.log(error))
        }
  render () {
    return (
      <div>
        <h1>Itunes Search</h1>
            <p>Search Here:</p>
            <form onSubmit={this.Search}>
              <label>
                Search :
                <input type="text" value={this.state.searchQuery} onChange={this.handleChange} />
              </label>
              <input type="submit"/>
            </form>
      </div>
    );
  }
}
