import React, { Component } from 'react'
import { withRouter } from 'react-router-dom'
import { Button, Input, InputGroup, InputGroupAddon, Container } from 'reactstrap'

class SearchItems extends Component {
  state = {
    Items: []
  }

  setQuery = event => {
    this.setState({ query: event.target.value })
  }

  search = () => {
    fetch(`https://localhost:44383/search/${this.state.query}`, { mode: 'cors' })
          .then(res => res.json())
          .then(({ results }) => this.setState({ Items: results }))
    }
  render() {
    return (
      <InputGroup>
        <Input placeholder="Enter a term..."  />
        <InputGroupAddon addonType="append">
          <Button color="secondary" onClick={this.search}>
            Search
          </Button>
        </InputGroupAddon>
         {/* <Container> */}
            {this.state.Items.map(Item => (
                <Item key={Item.trackId} item={Item} />
                ))}
        {/* </Container> */}
      </InputGroup>
    )
  }
}

export default withRouter(SearchItems)
