import React, { Component,Fragment } from 'react';
import { Container } from 'reactstrap'
//import SearchItems from './SearchItems'
import { item } from './Item'

//import axios from 'axios';

export class Searches extends Component {
    constructor(props) {
        super(props);
    
        this.state = {
            items: [],
        };
    }
    setQuery = event => {
        this.setState({ query: event.target.value });
    }
    componentDidMount() {
        this.search();
    }
    componentDidUpdate(prevProps) {
        if (this.props.location.search !== prevProps.location.search) {
            this.search()
        }
    }
    search = () => {
        //const { search: query } = this.props.location
        fetch(`https://localhost:44383/search/${this.state.query}`, { mode: 'cors' })
          .then(res => res.json())
          .then( data => this.setState({ items: data }))
         
      }


    render() {
        const { items } = this.state;
    return (
        <div>
            <div>
                <input placeholder="Enter a term..."  onChange={this.setQuery} />
                <button color="secondary" onClick={this.search}>
                    Search
                </button>
            </div>
            <Container>
                {items.map(item => (
                    <item key={item.trackId} Item={item} />))}
            </Container>
        </div>
    )
    }
}
