import React, { Component } from 'react'
import { withRouter } from 'react-router-dom'
import { Button, Input, InputGroup, InputGroupAddon } from 'reactstrap'

class SearchItems extends Component {
  state = {
    query: ''
  }

  setQuery = event => {
    this.setState({ query: event.target.value })
  }

  search = () => {
    this.props.history.push(
      `/tracks?term=${this.state.query.split(/ +/).join('+')}`
    )
    const { search: query } = this.props.location
    
        fetch(`https://localhost:44383/search/${this.state.query.split(/ +/).join('+')}`, { mode: 'cors' })
          .then(res => res.json())
          .then(({ results }) => this.setState({ Items: results }))
      };
  

  render() {
    return (
      <InputGroup>
        <Input placeholder="Enter a term..."  onChange={this.setQuery} />
        <InputGroupAddon addonType="append">
          <Button color="secondary" onClick={this.search}>
            Search
          </Button>
        </InputGroupAddon>
      </InputGroup>
    )
  }
}

export default withRouter(SearchItems)
