import React, { Component } from 'react';

export class Home extends Component {
  constructor(props) {
    super(props);
    //this.handleSearchTermSubmit = this.handleSearchTermSubmit.bind(this);
    this.state = {searchQuery: ''};
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  search(event) {
    this.setState({searchQuery: event.target.value});
    var query = `/tracks?term=${this.state.query.split(/ +/).join('+')}`
    console.log(query);
    
  }

  handleSubmit(event) { //SEND TO SERVER WITH THE QUERY
    alert('A searchQuery was submitted: ' + this.state.searchQuery);
    event.preventDefault();
  }
  render () {
    return (
      <div>
        <h1>Itunes Search</h1>
            <p>Search Here:</p>
            <form onSubmit={this.search}>
              <label>
                Search :
                <input type="text" value={this.state.searchQuery} onChange={this.handleChange} />
              </label>
              <input type="submit"/>
            </form>
      </div>
    );
  }
}
