import React, { Component } from 'react';
import axios from 'axios';

export class Home extends Component {
  constructor(props) {
    super(props);
    //this.handleSearchTermSubmit = this.handleSearchTermSubmit.bind(this);
    this.state = {searchQuery: ''};
    this.handleChange = this.handleChange.bind(this);
    this.Search = this.Search.bind(this);
  }

  handleChange(event) {
    this.setState({searchQuery: event.target.value});
  }

  Search(event) { //SEND TO SERVER WITH THE QUERY
    const apiBaseUrl = 'https://localhost:44383/search/';
    var searchQ = `${this.state.searchQuery.split(/ +/).join('+')}`;
    console.log(searchQ);
    alert('A searchQuery was submitted: ' + this.state.searchQuery);
    event.preventDefault();
    axios.post(apiBaseUrl, searchQ)
            .then(function (response) {
                console.log(response);
                if (response.data.code == 200) {
                    console.log(" successfull");
                    //var uploadScreen = [];
                    //uploadScreen.push(<UploadScreen appContext={self.props.appContext} />)
                    //self.props.appContext.setState({ loginPage: [], uploadScreen: uploadScreen })
                }
                else if (response.data.code == 204) {
                    console.log("Something Went Wrong");
                    alert("Something Went Wrong")
                }
                else {
                    console.log("USomething Went Wrong");
                    alert("Something Went Wrong");
                }
            })
            .catch(function (error) {
                console.log(error);
            });
  }
  render () {
    return (
      <div>
        <h1>Itunes Search</h1>
            <p>Search Here:</p>
            <form onSubmit={this.Search}>
              <label>
                Search :
                <input type="text" value={this.state.searchQuery} onChange={this.handleChange} />
              </label>
              <input type="submit"/>
            </form>
      </div>
    );
  }
}
