import React, { Component } from 'react';

export class TopSearch extends Component {
    constructor(props) {
        super(props);
    
        this.state = {
            loading: true,
            topSearch: [],
        };
    }
    componentDidMount() {
        this.getTop();
    }
    getTop() {
        var user = JSON.parse(localStorage.getItem('user'));
        this.setState({loading : true});
            fetch(`https://localhost:44383/search/top/${this.user.id}`, { mode: 'cors' })
              .then(res => res.json())
              .then( data => this.setState({ topSearch: data.results,loading: false }))
        }


    render() {
        return (
            <div>
                <h1>Top 10 Search by You:</h1>
            </div>
        );
    }
}
